CREATE TABLE IF NOT EXISTS `u_user` (
  `u_id` int(6) unsigned NOT NULL AUTO_INCREMENT,
  `u_username` varchar(30) NOT NULL,
  `u_password` varchar(34) NOT NULL,
  `u_email` varchar(50) NOT NULL,
  `u_tel` varchar(11) NOT NULL,
  `u_area` varchar(30) NOT NULL,
  `u_sex` varchar(3) NOT NULL,
  `u_hobby` varchar(30) NOT NULL,
  `u_reg_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`u_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

